import UIKit

/// Use these CGFloat values when specifying spacing and padding in UIViews.
extension CGFloat {
    static public let spacing0: Self = 0
    static public let spacing4: Self = 4
    static public let spacing8: Self = 8
    static public let spacing12: Self = 12
    static public let spacing16: Self = 16
    static public let spacing20: Self = 20
    static public let spacing24: Self = 24
    static public let spacing28: Self = 28
    static public let spacing32: Self = 32
    static public let spacing36: Self = 36
    static public let spacing40: Self = 40
    static public let spacing48: Self = 48
    static public let spacing56: Self = 56
    static public let spacing64: Self = 64
    static public let spacing72: Self = 72
    static public let spacing80: Self = 80
    static public let spacing128: Self = 128
}
